(151, 7, '2012-01-23 15:35:48', '0000-00-00 00:00:00', '<pre></pre>\r\n<pre><span style="font-family: Times New Roman,serif"><span style="font-size: medium">Je t''aime Vieux Québec</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Pour tes côtes abruptes </span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Pour ma perte de poids </span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">après la grossesse</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Je t''aime Vieux Québec</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Pour ta pataugeoire trop froide</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">du Parc de L''Esplanade </span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Pour ton odeur de cheval </span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">au gros soleil</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Je t''aime Vieux Québec</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Pour la cornemuse, les vendredis d''été,</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">dans la cour du Morrin Center</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Je t''aime Vieux Québec</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Pour tes caricatures d''ados de régions</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Pour tes t-shirts qui sacrent pis qui coutent cher</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Je t''aime Vieux Québec</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Pour tes Bentley, tes Ferrari sur rue Saint-Louis</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Pour ma poussette sur Du Trésor</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">et l''Écolobus</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">qui ne prend pas Mastercard</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Je t''aime Vieux Québec</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Pour m''empêcher de rentrer chez moi </span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">quand William et Kate viennent dire bonjour</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Je t''aime Vieux Québec</span></span>\r\n\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Parfois jeune, parfois vieux</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Parfois français, parfois chinois</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Parfois chic, parfois ontarien</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">\r\nJe t''aime</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Je t''aime \r\net je te jure fidélité</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">\r\nNon c''est promis</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">Je ne vivrai jamais</span></span>\r\n<span style="font-family: Times New Roman,serif"><span style="font-size: medium">\r\ndans le bout de Vanier</span></span></pre>\r\n<pre><span style="font-family: Times New Roman,serif"><span style="font-size: x-small"><br /></span></span></pre>', 'Moi, j''aime un vieux', '', 'pending', 'open', 'open', '', '', '', '', '2012-01-23 15:35:48', '2012-01-23 20:35:48', '', 0, 'http://localhost:2200/?p=151', 0, 'post', '', 0),
(152, 7, '2012-01-23 15:32:43', '2012-01-23 20:32:43', '<pre><span style="font-family: Times New Roman,serif"><span style="font-size: x-small">Je t''aime Vieux Québec</span></span>\n\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">Pour tes côtes abruptes </span></span>\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">Pour ma perte de poids </span></span>\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">après la grossesse</span></span>\n\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">Je t''aime Vieux Québec</span></span>\n\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">Pour ta pataugeoire trop froide</span></span>\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">du Parc de L''Esplanade </span></span>\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">Pour ton odeur de cheval </span></span>\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">au gros soleil</span></span>\n\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">Je t''aime Vieux Québec</span></span>\n\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">Pour la cornemuse, les vendredis d''été,</span></span>\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">dans la cour du Morrin Center</span></span>\n\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">Je t''aime Vieux-Québec</span></span>\n\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">Pour tes caricatures d''ados de régions</span></span>\n<span style="font-family: Times New Roman,serif"><span style="font-size: x-small">Pour tes t-shirts qui sacrent pis qui coutent cher</span></span>\n\n<span style="font-family: Times New Roman,se